mapper.select:
SELECT m.confine_status,m.rank_integral,m.`id`,m.`dis_platform_id`,m.`dis_user_id`,m.`dis_parent_id`,m.`dis_full_index`,m.`dis_user_name`,m.`update_time`
,m.`dis_note`,m.`add_time`,d.`dic_value` AS dis_user_type,f.`dic_value` AS dis_user_rank
FROM  `dis_member_info` m
LEFT JOIN `sys_dic` d ON m.`dis_user_type`=d.`dic_no` AND d.`dic_type_no`='disUserPlatType' AND d.`is_delete`='N'

LEFT JOIN `sys_dic` f ON m.`dis_user_rank`=f.`dic_no` AND f.`dic_type_no`='disUserRank' AND f.`is_delete`='N'
WHERE m.is_delete='N' and m.identity_type='0'


    and    m.dis_plat_super=#{account}


    and    m.dis_user_id=#{disUserId}


    and    m.dis_parent_id=#{disModelId}

order by m.add_time DESC
