mapper.select:
SELECT p.`id`,p.`dis_platform_id`,p.dis_rank_name, e.`dic_value` AS cal_model,d.`dic_value` AS dis_pro_level, f.`dic_value` AS account_type ,p.`add_time`,p.`dis_integral_value` ,g.`dic_value` dis_user_type,h.`dic_value` dis_user_rank FROM `dis_rank_param` p LEFT JOIN `sys_dic` d ON d.`dic_no`=p.`dis_pro_level` AND d.`dic_type_no`='disRankLevel' LEFT JOIN `sys_dic` e ON e.`dic_no`=p.`cal_model` AND e.`dic_type_no`='calModel' LEFT JOIN `sys_dic` f ON f.`dic_no`=p.`account_type` AND f.`dic_type_no`='accountType' LEFT JOIN `sys_dic` g ON g.`dic_no`=p.`dis_user_type` AND g.`dic_type_no`='disUserPlatType' LEFT JOIN `sys_dic` h ON h.`dic_no`=p.`dis_user_rank` AND h.`dic_type_no`='disUserRank' WHERE p.is_delete='N'
and p.dis_platform_id=#{account}
and p.cal_model=#{calModel}
and p.account_type=#{accountType}
and p.dis_user_type=#{disUserType}
and p.dis_user_rank=#{disUserRank}
order by p.add_time DESC
