mapper.select:
SELECT u.`begin_integral`,u.`id`,u.`end_integral`,u.`add_time`,u.`upgrade_name`, e.`dic_no` dis_user_rank FROM `dis_upgrade_param` u LEFT JOIN `sys_dic` e ON e.`dic_no`=u.`dis_user_rank` AND e.`dic_type_no`='disUserRank' WHERE u.`is_delete`='N' and identity_type='0'
and u.upgrade_name LIKE #{upgradeName}
order by u.`add_time` desc

SELECT u.`begin_integral`,u.`id`,u.`end_integral`,u.`add_time`,u.`upgrade_name`, e.`dic_no` dis_user_rank FROM `dis_upgrade_param` u LEFT JOIN `sys_dic` e ON e.`dic_no`=u.`dis_user_rank` AND e.`dic_type_no`='agentRank' WHERE u.`is_delete`='N' and identity_type='1'
and u.upgrade_name LIKE #{upgradeName}
order by u.`add_time` desc
